    import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

function Admin() {
  const [messages, setMessages] = useState([]);
  const [messagesOut, setMessagesOut] = useState('');
  const [selectedUser, setSelectedUser] = useState(null);
  const [joinedUsers, setJoinedUsers] = useState([]);
  const sockets = io('http://localhost:3001');

  useEffect(() => {
    const socket = io('http://localhost:3001');
    socket.emit('getJoinedUsers');

    socket.on('joinedUsers', (users) => {
      const uniqueUsernames = new Set();
      const filteredUsers = users.filter((user) => {
        if (!uniqueUsernames.has(user.username) && user.username) {
          uniqueUsernames.add(user.username);
          return true;
        }
        return false;
      });
      setJoinedUsers(filteredUsers);
    });
    socket.on('adminMessage', (data) => {
      const { userId, message } = data;
      setMessages((prevMessages) => [
        ...prevMessages,
        { userId: 'admin', message },
      ]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleUserSelect = (user) => {
    setSelectedUser(user);
    setMessages((prevMessages) =>
      prevMessages.filter((message) => message.userId === user.id)
    );
  };

  const handleMessageChange = (event) => {
    setMessagesOut(event.target.value);
  };

  const handleSendMessage = () => {
    if (selectedUser && messagesOut) {
      sockets.emit('adminMessageOut', {
        id: selectedUser.id,
        username: selectedUser.username,
        message: messagesOut,
      });
      setMessagesOut('');
    }
  };

  return (
    <div className="flex">
      <div className="w-1/4 bg-gray-200 p-4">
        <h3 className="text-xl mb-4">Joined Users</h3>
        <ul>
          {joinedUsers.map((user) => (
            <li
              key={user.id}
              className={`cursor-pointer p-2 mb-2 ${
                user === selectedUser ? 'bg-blue-500 text-white' : 'bg-white'
              }`}
              onClick={() => handleUserSelect(user)}
            >
              {user.username}
            </li>
          ))}
        </ul>
      </div>

      <div className="flex-1 p-4">
        <h3 className="text-xl mb-4">Chat</h3>
        {selectedUser ? (
          <div>
            <h4 className="text-lg mb-2">Selected User: {selectedUser.username}</h4>
            <div className="bg-white p-4 h-80 overflow-y-auto">
            {messages
  .filter((message) => message.userId === 'admin' || message.userId === selectedUser?.id)
  .map((message, index) => (
    <div key={index} className="mb-2">
      {message.userId === 'admin' ? (
        <strong>Admin: </strong>
      ) : (
        <strong>User {message.userId}: </strong>
      )}
      {message.message}
    </div>
))}
            </div>
            <div className="mt-4">
              <input
                type="text"
                value={messagesOut}
                onChange={handleMessageChange}
                className="p-2 border rounded mr-2"
                placeholder="Enter your message"
              />
              <button
                onClick={handleSendMessage}
                className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
              >
                Send Message
              </button>
            </div>
          </div>
        ) : (
          <p>No user selected.</p>
        )}
      </div>
    </div>
  );
}

export default Admin;
